import React, { useState } from 'react';
import {
    View,
    ScrollView,
    KeyboardAvoidingView,
    Alert,
    SafeAreaView, StyleSheet,
} from 'react-native';

import Mytextinput from '../components/Mytextinput';
import Mybutton from '../components/Mybutton';
import { DatabaseConnection } from '../../database/database-connection';
import {TextInputMask} from "react-native-masked-text";

const db = DatabaseConnection.getConnection();

const UpdateServicoRealizado = ({ navigation }) => {
    let [inputServicoId, setInputServicoId] = useState('');
    let [clienteNome, setClienteNome] = useState('');
    let [servicoPreco, setServicoPreco] = useState('');

    let updateAllStates = (name, contact, address) => {
        setClienteNome(name);
        setServicoPreco(contact);
    };

        let searchUser = () => {
        console.log(inputServicoId);
        db.transaction((tx) => {
            tx.executeSql(
                'SELECT tbs.servico_realizado_id, tbs.cliente_nome, tf.funcionario_nome, tts.nome_servico, tts.preco_servico FROM table_servico_realizado tbs, table_funcionario tf, table_tipo_servico tts WHERE tbs.servico_realizado_id = ? AND tbs.funcionario_id = tf.funcionario_id AND tbs.tipo_servico_id = tts.tipo_servico_id',
                [inputServicoId],
                (tx, results) => {
                    var len = results.rows.length;
                    if (len > 0) {
                        let res = results.rows.item(0);
                        updateAllStates(
                            res.nome_servico,
                            res.preco_servico,
                        );
                    } else {
                        alert('Tipo de Servico não encontrado!');
                        updateAllStates('', '', '');
                    }
                }
            );
        });
    };
    let updateUser = () => {
        console.log(inputServicoId, clienteNome, servicoPreco);

        if (!inputServicoId) {
            alert('Por Favor informe o Código!');
            return;
        }
        if (!clienteNome) {
            alert('Por favor informe o Nome do Serviço !');
            return;
        }
        if (!servicoPreco) {
            alert('Por Favor informe o Preço do Serviço !');
            return;
        }

        db.transaction((tx) => {
            tx.executeSql(
                'UPDATE table_servico_realizado set cliente_nome=?, funcionario_id=? where tipo_servico_id=?',
                [clienteNome, servicoPreco, inputServicoId],
                (tx, results) => {
                    console.log('Results', results.rowsAffected);
                    if (results.rowsAffected > 0) {
                        Alert.alert(
                            'Sucesso',
                            'Serviço Realizado atualizado com sucesso !!',
                            [
                                {
                                    text: 'Ok',
                                    onPress: () => navigation.navigate('HomeScreenTipoServico'),
                                },
                            ],
                            { cancelable: false }
                        );
                    } else alert('Erro ao atualizar o Tipo de serviço');
                }
            );
        });
    };

    return (
        <SafeAreaView style={{ flex: 1 }}>
            <View style={{ flex: 1, backgroundColor: 'white' }}>
                <View style={{ flex: 1 }}>
                    <ScrollView keyboardShouldPersistTaps="handled">
                        <KeyboardAvoidingView
                            behavior="padding"
                            style={{ flex: 1, justifyContent: 'space-between' }}>
                            <Mytextinput
                                placeholder="Digite o Código do Serviço Realizado"
                                style={{ padding: 10 }}
                                onChangeText={
                                    (inputServicoId) => setInputServicoId(inputServicoId)
                                }
                            />
                            <Mybutton
                                title="Buscar Tipo de Serviço"
                                customClick={searchUser}
                            />
                            <Mytextinput
                                placeholder="Digite o Nome do Cliente"
                                value={clienteNome}
                                style={{ padding: 10 }}
                                onChangeText={
                                    (clienteNome) => setClienteNome(clienteNome)
                                }
                            />
                            <Mybutton
                                title="Atualizar Tipo de Serviço"
                                customClick={updateUser}
                            />
                        </KeyboardAvoidingView>
                    </ScrollView>
                </View>
            </View>
        </SafeAreaView>
    );
};

const styles = StyleSheet.create({
    inputText: {
        marginLeft: 25,
        marginRight: 25,
        marginTop: 10,
        borderColor: '#00AD98',
        borderWidth: 1,
        height: 50,
        padding: 10,
        borderRadius: 20,
    },
});

export default UpdateServicoRealizado;
