import React,{ useEffect }  from 'react';
import { View, SafeAreaView } from 'react-native';
import MyImageButton from './components/MyImageButton';
import {DatabaseConnection} from "../database/database-connection";

const db = DatabaseConnection.getConnection();

const Home = ({ navigation }) => {

    useEffect(() => {
        db.transaction(function (txn) {
            txn.executeSql(
                "SELECT name FROM sqlite_master WHERE type='table' AND name='table_funcionario'",
                "SELECT name FROM sqlite_master WHERE type='table' AND name='table_tipo_servico'",
                [],
                function (tx, res) {
                    console.log('item:', res.rows.length);
                    if (res.rows.length == 0) {
                        txn.executeSql('DROP TABLE IF EXISTS table_funcionario', []);
                        txn.executeSql('DROP TABLE IF EXISTS table_tipo_servico', []);
                        txn.executeSql(
                            'CREATE TABLE IF NOT EXISTS table_funcionario(funcionario_id INTEGER PRIMARY KEY AUTOINCREMENT, funcionario_nome VARCHAR(99), funcionario_telefone INT(11))',
                            []
                        );
                        txn.executeSql(
                            'CREATE TABLE IF NOT EXISTS table_tipo_servico(tipo_servico_id INTEGER PRIMARY KEY AUTOINCREMENT, nome_servico VARCHAR(99), preco_servico VARCHAR(6))',
                            []
                        );
                    }
                }
            );
        });
    }, []);

    return (
        <SafeAreaView style={{ flex: 1 }}>
            <View style={{ flex: 1, backgroundColor: 'white' }}>
                <View style={{ flex: 1 }}>
                    <View style={{ flex: 1 }}>

                        <MyImageButton
                            title="Registro de Funcionário"
                            btnColor='#2992C4'
                            btnIcon="check"
                            customClick={() => navigation.navigate('HomeScreenFuncionario')}
                        />

                        <MyImageButton
                            title="Registro de Tipo de Serviço"
                            btnColor='#00FA9A'
                            btnIcon="check"
                            customClick={() => navigation.navigate('HomeScreenTipoServico')}
                        />

                        <MyImageButton
                            title="Registrar Serviço Realizado"
                            btnColor='#FFD700'
                            btnIcon="check"
                            customClick={() => navigation.navigate('HomeScreenTipoServico')}
                        />
                    </View>
                </View>


            </View>
        </SafeAreaView>
    );
};

export default Home;
